import React, {Component} from "react";

class pricing extends Component {
  constructor() {
    super();
    this.state = {
         cart1 : [
           {
             product: "Fancy Product",
             priceRange: "$40.00 - $80.00"
           },
           {
            product: "Special Item",
            priceRange: "$18.00"
           },
           {
            product: "Sale Item",
            priceRange: "$25.00"
           },
           {
            product: " Popular Item",
            priceRange: " $40.00"   
           },
          ],
      cart2:[
           {
            product: " Sale Item",
            priceRange: " $25.00"   
           },
           {
            product: "Fancy Product",
            priceRange: "$120.00 - $280.00"
           },
           {
            product: "Special Item",
            priceRange: "$18.00"
           },
           {
            product: " Popular Item",
            priceRange: " $40.00"   
           }
         ]
    }
  }
  render () {
         return(
           <>
           <div style={{textAlign:"center", background:"black", color:"white"}}>
             <h1>Shop In Style</h1>
           </div>
            <div style={{textAlign:"center"}}>
             <div class="row">
             {this.state.cart1.map(({product,priceRange})=>
              <div class="col" style={{border:"1px solid grey"}}>
                <img src="https://www.jaipuriaschoolbahraich.in/wp-content/uploads/2016/11/blank-img.jpg" height="200" width="300" alt="product" />
                <br />
                <span>{product}</span>
                <br />
                <span>{priceRange}</span>
              </div>
             )}
             </div>
             <div class="row">
             {this.state.cart2.map(({product,priceRange})=>
              <div class="col" style={{border:"1px solid grey"}}>
                <img src="https://www.jaipuriaschoolbahraich.in/wp-content/uploads/2016/11/blank-img.jpg" height="200" width="300" alt="product" />
                <br />
                <span>{product}</span>
                <br />
                <span>{priceRange}</span>
              </div>
             )}
             </div>
             </div>
           </>
         )
  }
}
export default pricing;
