import React, {Component} from "react";

class pricing extends Component {
  constructor() {
    super();
    this.state = {
         cart1 : [
           {
             product: "Fancy Product",
             priceRange: "$40.00 - $80.00"
           },
           {
            product: "Special Item",
            priceRange: "$18.00"
           },
           {
            product: "Sale Item",
            priceRange: "$25.00"
           },
           {
            product: " Popular Item",
            priceRange: " $40.00"   
           },
          ],
      cart2:[
           {
            product: " Sale Item",
            priceRange: " $25.00"   
           },
           {
            product: "Fancy Product",
            priceRange: "$120.00 - $280.00"
           },
           {
            product: "Special Item",
            priceRange: "$18.00"
           },
           {
            product: " Popular Item",
            priceRange: " $40.00"   
           }
         ]
    }
  }
  render () {
         return(
           <>

             <div class="row">
             {this.state.cart1.map(({product,priceRange})=>
              <div class="col">
                <img src="https://www.jaipuriaschoolbahraich.in/wp-content/uploads/2016/11/blank-img.j" height="200" width="300" alt="product" />
                <span>{product}</span>
                <span>{priceRange}</span>
              </div>
             )}
             </div>

           </>
         )
  }
}
export default pricing;
